entry.attribute:
class CLASSNAME implements iAchievement {
	public function run(Achievement $A, AchievementStatus $S, array $args) {
		$S = Stammdaten::getActiveStammdaten();
		if($S->A("land") == "")
			return false;

		if($args[0]->land == "")
			return false;


		if(ISO3166::getCodeToCountry($args[0]->land) != $S->A("land"))
			return true;

		return false;
	}
}
